import torch
import torch.nn as nn

# Define the model
class MentalHealthPredictor(nn.Module):
    def __init__(self):
        super(MentalHealthPredictor, self).__init__()
        self.fc1 = nn.Linear(3, 64)  # Example input size
        self.fc2 = nn.Linear(64, 1)  # Example output size

    def forward(self, x):
        x = torch.relu(self.fc1(x))
        x = self.fc2(x)
        return x

# Initialize and save the model
model = MentalHealthPredictor()
torch.save(model.state_dict(), 'mental_health_model.pth')

